async def list_types(callback: types.CallbackQuery, worker: str, **kwargs):
    await callback.message.edit_media(
        media=types.InputMediaPhoto(
            media=types.InputFile(NATALIA),
            caption="–í—ã–±–µ—Ä–∏—Ç–µ –Ω—É–∂–Ω—É—é –∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü–∏—é:"
        ),
        reply_markup=types.InlineKeyboardMarkup(row_width=1).add(
            *[
                types.InlineKeyboardButton(
                    text=value["text"],
                    callback_data=inline.make_free_cd(level=2, worker=worker, type=key),
                )
                for key, value in frees.items()
            ],
            types.InlineKeyboardButton(
                text="–ù–∞–∑–∞–¥",
                callback_data=inline.make_free_cd(level=0, worker=worker),
            )
        ),
    )


async def show_type(callback: types.CallbackQuery, worker: str, type: str, **kwargs):
    await callback.message.edit_media(
        media=types.InputMediaPhoto(
            media=types.InputFile(frees[type]["description_media"]),
            caption=frees[type]["description"],
        ),
        reply_markup=types.InlineKeyboardMarkup(row_width=2).add(
            *[
                types.InlineKeyboardButton(
                    text=option.capitalize(),
                    callback_data=inline.make_free_cd(
                        level=3, worker=worker, type=type, option=option
                    ),
                )
                for option in frees[type]["options"]
            ],
            types.InlineKeyboardButton(
                text="–ù–∞–∑–∞–¥",
                callback_data=inline.make_free_cd(level=1, worker=worker),
            )
        )
    )


async def show_option(
    callback: types.CallbackQuery, worker: str, type: str, option: str
):
    await callback.message.edit_caption(
        frees[type]["options"][option],
        reply_markup=types.InlineKeyboardMarkup(row_width=1).add(
            types.InlineKeyboardButton(
                text="–ù–∞–∑–∞–¥",
                callback_data=inline.make_free_cd(level=2, worker=worker, type=type),
            )
        ),
    )


@dp.callback_query_handler(inline.free_cd.filter())
async def free_navigate(callback: types.CallbackQuery, callback_data: dict) -> None:
    level = callback_data.get("level")
    worker = callback_data.get("worker")
    type = callback_data.get("type")
    option = callback_data.get("option")

    levels = {"0": start, "1": list_types, "2": show_type, "3": show_option}

    current_level_function = levels[level]

    await current_level_function(callback, worker=worker, type=type, option=option)
